import React from 'react';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image';
import roadTrip from './assets/roadTrip.jpg';
import styled from 'styled-components';

const Styles = styled.div`
    .container {
        padding: 7em 7em;
        justify-content: center;
        align-items: center;
    }
    .body {
        text-align: left;
        line-height: 1.3;
        font-size: x-large;
    }
    .coverPhoto {
        height: 100%;
        width: 100%;
        // opacity: 0.4;
    }
    .title {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        z-index: 2;
        font-family: Georgia, serif;
        font-size: xxx-large;
        font-weight: 900;
    }
    .titleAndDateContainer {
        position: relative;
        text-align: center;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        z-index: -1;
    }
    .imageContainer {
        position: relative;
        text-align: center;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        z-index: -1;
    }
    .overlay {
        color: #000;
        opacity: 1.0;
        position: relative;
        width: 100%;
        height: 100%;
        z-index: -1;
    }
`;

export default class Article extends React.Component {

    constructor(props) {
        super(props)

        this.state = {article: props.location.state}
    }

    render() {
        if (this.state === null) return <p>Loading ...</p>;
        if (this.state) {
            return (
                <Styles>
                    <br></br>
                    <br></br>
                    <div className="container">
                        <div className="imageContainer">
                            {/* <img className="coverPhoto" src="https://images.unsplash.com/photo-1530089711124-9ca31fb9e863?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2849&q=80.jpg"></img> */}
                            <img className="coverPhoto" src={this.state.article.article.coverPhotoLink}></img>
                        </div> 
                        <br></br>
                        <br></br>
                        <br></br>
                        <br></br>
                        <div className="titleAndDateContainer">
                            <h2 className="title">{this.state.article.article.title}</h2>
                        </div>
                        <br></br>
                        <br></br>
                        <br></br>
                        <div className="body" dangerouslySetInnerHTML={{__html: this.state.article.article.text}}/>
                    </div>
                </Styles>
            )
        }
    }
}
