import React from 'react';
import { Jumbotron as Jumbo, Container} from 'react-bootstrap';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import styled from 'styled-components';
import roadTrip from '../assets/roadTrip.jpg';
import { Link } from 'react-router-dom';
import Articles from '../Articles.js';

const Styles = styled.div`
  .jumbo {
    background-size: cover;
    background-color: #ffffff;
    color: #000000;
    height: 150px;
    position: relative;
    z-index: 1;
    text-align: center;
  }
  .overlay {
    // background-color: #000;
    // opacity: 0.6;
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    z-index: -1;
  }
  .link{
    color: #000000;
    text-decoration: none;
  }
`;

export default class Jumbotron extends React.Component {

  render() {

    let header;
    if (this.props.link) {
      header = <Link to={{pathname: `${this.props.link}`, state: {showArticleTable: true}}} className="link"><h1>{this.props.title}</h1></Link>
      console.log("there is a link")
    } else{
      header = <h1>{this.props.title}</h1>
      console.log("there is no link")
    }

    return (
      <Styles>
      <Jumbo fluid className="jumbo">
        <div className="overlay"></div>
        <Container>
          {header}
          <p>{this.props.subtitle}</p>
        </Container>
      </Jumbo>
      </Styles>
    )
  }
}
